  const [on, setOn] = useState(true)
  return (
    <>
      <button
        onClick={() => {
          setObj({ like: obj.like + 1, on: obj.on });
        }}
      >
        {obj.like}👍🏻
      </button>
      <button
        onClick={() => {
          setObj({ like: obj.like, on: !obj.on });
        }}
      >
        {obj.on ? 'ON' : 'OFF'}
      </button>
    </>
  );
};
export default LikeButton;
